tab.script:
document.body.scrollLeft = document.body.clientWidth / 2;

const content = document.getElementById('content'),
	select = document.getElementById('select');
select.style.marginTop = -select.offsetHeight / 2 + 'px';
select.style.marginLeft = -select.offsetWidth / 2 + 'px';
select.style.display = 'none';

Array.from(content.children).forEach(p => p.addEventListener('click', () => {
	if (content.style.filter === 'none' || !content.style.filter) {
		content.style.filter = 'brightness(10%)';
		select.style.display = 'inline';
	} else {
		content.style.filter = select.style.display = 'none';
	}
}));

Array.from(select.children).forEach(p => p.addEventListener('click', () => {
	if (p.style.filter === 'none' || !p.style.filter) {
		p.style.filter = 'brightness(30%)';
		document.getElementById(p.innerText).style.display = 'none';
	} else {
		p.style.filter = 'none';
		document.getElementById(p.innerText).style.display = 'block';
	}
	document.body.scrollTop = 0;
}));
