tab.script:
const content = document.getElementById('content'),
	select = document.getElementById('select');

content.addEventListener('click', () => {
	if(content.style.filter) content.style.filter = select.style.display = '';
	else{
		content.style.filter = 'brightness(10%)';
		select.style.display = 'inline';
	}
});
Array.from(select.getElementsByTagName('p')).forEach(p => p.addEventListener('click', () => {
	p.classList.toggle('off');
	document.getElementById(p.innerText).style.display = p.classList.contains('off') ? 'none' : 'block';
}));

document.getElementById('inverse').addEventListener('click', () => Array.from(select.getElementsByTagName('p')).forEach(p => p.click()));
